.container{max-width:1100px;margin:0 auto;padding:16px;}

.site-header{
  background:#fff;padding:14px 0;border-bottom:1px solid #eee;
}
.brand{margin:0;font-size:1.25rem;color:var(--accent)}

.hero{
  background: radial-gradient(circle, #ffe3ec, #fff);
  padding:28px;border-radius:var(--radius);text-align:center;margin:16px 0;
}

.catalog{
  display:grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap:16px;
  margin:16px 0;
}

/* product card */
