#!/usr/bin/env bash
set -e

# Creates the cherry-parfumerie project files and a cherry-parfumerie.zip archive.
# Usage:
# 1) Save this script as cherry-parfumerie-setup.sh
# 2) chmod +x cherry-parfumerie-setup.sh
# 3) ./cherry-parfumerie-setup.sh
#
# The script will create a folder ./cherry-parfumerie and cherry-parfumerie.zip

OUTDIR="cherry-parfumerie"
rm -rf "$OUTDIR"
mkdir -p "$OUTDIR/images"
mkdir -p "$OUTDIR/.github/workflows"

echo "Creating files in ./$OUTDIR ..."

