.btn{padding:10px 12px;border-radius:8px;border:0;cursor:pointer;font-weight:600}
.btn:focus{outline:3px solid rgba(216,27,96,0.15)}
.btn-primary{background:var(--accent);color:#fff}
.btn-outline{background:#fff;color:var(--accent);border:1px solid var(--accent)}

/* cart (aside) */
.cart{
  position:fixed;
  right:16px;
  top:80px;
  width:320px;
  max-height:70vh;
  overflow:auto;
  background:var(--card-bg);
  border-radius:12px;
  box-shadow:0 6px 22px rgba(0,0,0,0.08);
  padding:12px;
  border:1px solid #eee;
  z-index:1000;
}
.cart h3{margin-top:0}
.cart-item{display:flex;gap:8px;align-items:center;padding:8px 0;border-bottom:1px dashed #f2f2f2}
.cart-item img{width:56px;height:56px;object-fit:cover;border-radius:8px}
.qty-controls{display:flex;gap:6px;align-items:center}
.qty-controls button{width:28px;height:28px;padding:0}

/* checkout panel */
.checkout{
  position:fixed;
  left:50%;
  top:50%;
  transform:translate(-50%,-50%);
  background:var(--card-bg);
  padding:18px;
  border-radius:12px;
  box-shadow:0 8px 38px rgba(0,0,0,0.12);
  width:90%;
  max-width:520px;
  z-index:1100;
}
.hidden{display:none}

.checkout label{display:block;margin-bottom:8px}
.checkout input, .checkout textarea{
  width:100%;padding:8px;border-radius:8px;border:1px solid #eee;margin-top:6px;
}

/* responsive: hide cart on small screens; toggle button opens it */
@media (max-width:900px){
  .cart{right:8px;top:auto;bottom:16px;width:calc(100% - 32px);max-height:50vh}
}
EOF

